pg.init()

sw = 1600
sh = 900


map = pg.Surface((tw*tile_size*map_pixel_size,th*tile_size*map_pixel_size))
tiles.render(map)

screen = pg.display.set_mode((sw,sh))

fps = 60
cam_pos = Vector2(0,0)
fps_clock = pg.time.Clock()

running = True
while running:
    cam_pos += ((player.pos*(tile_size*map_pixel_size)) - cam_pos)*0.1
    events = pg.event.get()
    for event in events:
        if event.type == pg.QUIT:
            running = False

    
    update_pressed()
    screen.fill(tile_colors[3])
    screen.blit(map,(-cam_pos.x+sw/2,-cam_pos.y+sh/2))
    player.update()
    pg.draw.rect(screen,tile_colors[4],pg.Rect((Vector2(sw,sh)/2-Vector2(player.pw,player.ph)*(tile_size*map_pixel_size)/2)-(cam_pos-player.pos*(tile_size*map_pixel_size)),Vector2(player.pw,player.ph)*(tile_size*map_pixel_size)))
    pg.display.update()
    fps_clock.tick(fps)
pg.quit()